    constructor(props){
        super(props);
        this.state = {activeTab: 0};
    }
toggleCategories(){
    if(this.state.activeTab === 0){
        return(
            <div> <h1> This is react</h1></div>
        )
    }
}

    render() {
      return(
          <div className="category-tabs"> 

          <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({activeTba: tabId})}>
          <Tab> React</Tab>
          <Tab> Angular</Tab>
          <Tab> VueJS</Tab>
          <Tab> MongoDB</Tab>

          </Tabs>

          <section claassName="projects-grid">
          {this.toggleCategories()}

          </section>

          </div>
      )
    }
}

export default Projects;
