import React, { Component } from 'react';
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardText, CardActions, Button, CardMenu, IconButton } from 'react-mdl';


class Projects extends Component {
    constructor(props) {
        super(props);
        this.state = { activeTab: 0 };
    }
    toggleCategories() {
        if (this.state.activeTab === 0) {
            return (


                < div className="projects-grid" >
                    {/* Project 1 */}
                    <Card shadow={5} style={{ minWidth: '450', margin: 'auto' }}>
                        <CardTitle style={{ color: '#fff', height: '176px', background: 'url(https://xtnotes-1255646395.coshk.myqcloud.com/images/react-1.svg) center / cover' }}>BookShare Project</CardTitle>
                        <CardText> Technology used: MySql, PHP, BootStap3 </CardText>

                        <CardActions border>
                            <Button colored href="https://github.com/Beverlyckh/BookShare" rel="noopener noreferrer" target="_blank">GitHub </Button>
                            <Button colored href="https://drive.google.com/file/d/1srwxHSzt-P_2QlFEw5C3oQNjfo0TXbp2/view" rel="noopener noreferrer" target="_blank">Demo</Button>
                        </CardActions>



                    </Card>

                    {/* Project 2 */}


                    <Card shadow={5} style={{ minWidth: '450', margin: 'auto' }}>
                        <CardTitle style={{ color: '#fff', height: '176px', background: 'url(https://xtnotes-1255646395.coshk.myqcloud.com/images/react-1.svg) center / cover' }}></CardTitle>
                        <CardText> Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's
                </CardText>

                        <CardActions border>
                            <Button colored>GitHub </Button>
                            <Button colored>CodePen</Button>
                            <Button colored>LiveDemo </Button>
                        </CardActions>

                        <CardMenu style={{ color: '#fff' }}>
                            <IconButton name="share" />
                        </CardMenu>

                    </Card>

                    {/* Project 3 */}

                    <Card shadow={5} style={{ minWidth: '450', margin: 'auto' }}>
                        <CardTitle style={{ color: '#fff', height: '176px', background: 'url(https://xtnotes-1255646395.coshk.myqcloud.com/images/react-1.svg) center / cover' }}>ReactProject #1</CardTitle>
                        <CardText> Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's
            </CardText>

                        <CardActions border>
                            <Button colored>GitHub </Button>
                            <Button colored>CodePen</Button>
                            <Button colored>LiveDemo </Button>
                        </CardActions>

                        <CardMenu style={{ color: '#fff' }}>
                            <IconButton name="share" />
                        </CardMenu>

                    </Card>
                </div>
            )

        } else if (this.state.activeTab === 1) {
            return (
                <h1> Test </h1>)
        } else if (this.state.activeTab === 2) {
            return (
                <h1> Test </h1>
            )
        } else if (this.state.activeTab === 3) {
            return (

                <h1> Test </h1>
            )
        }
    }

    render() {
        return (
            <div>

                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })}>
                    <Tab> React</Tab>
                    <Tab> Angular</Tab>
                    <Tab> VueJS</Tab>
                    <Tab> MongoDB</Tab>

                </Tabs>

                <section className="projects-grid">
                    <Grid >

                        <Cell col={12}>
                            <div className="content">{this.toggleCategories()}</div>
                        </Cell>
                    </Grid>
                </section>




            </div>
        )
    }
}

export default Projects;
